import React, { useState } from 'react'
import Bg from '../Images/terrier.jpeg'
import { motion, AnimatePresence } from "framer-motion";
import flap from '../Images/Polygon_1.png'
import Form from '../components/form';

const Home = () => {
  const [showForm, setShowForm] = useState(false);

  return (
    <div className='flex items-center justify-center w-full h-screen' style={{ perspective: '1000px' }}>
      <motion.div 
        className='relative w-[700px]'
        animate={showForm ? { opacity: 0, z: -200 } : { z: 0 }}
        transition={{ duration: 1, delay: 1 }}
        style={{ transformStyle: 'preserve-3d' }}
      >
        <motion.div
          className='w-[700px] h-[100px] absolute top-0 left-0'
          animate={{ rotateX: 180, z: showForm ? -100 : 0 }}
          transition={{
            duration: 2,
            ease: "easeIn",
          }}
          style={{ 
            transformOrigin: "top",
            backgroundImage: `url(${flap})`, 
            backgroundSize: 'cover', 
            backgroundPosition: 'center',
            zIndex: 2
          }}
          onAnimationComplete={() => {
            setShowForm(true);
          }}
        /> 
        <div className='relative flex flex-col w-[700px] h-[400px] bg-[#FE5151] overflow-hidden'
        style={{ zIndex: 1 }}>
          <motion.div
            className="absolute top-0 left-0 right-0 w-0 h-0 mx-auto
                      border-l-[350px] border-l-transparent 
                      border-r-[350px] border-r-transparent 
                      border-t-[80px] border-t-[#eeff41] filter drop-shadow-[0_5px_5px_rgba(0,0,0,0.6)]"
            animate={{ rotateX: 180, z: showForm ? -50 : 0 }}
            transition={{
              duration: 2,
              ease: "easeIn",
            }}
            style={{ 
              transformOrigin: "top",
              zIndex: 3
            }}
          />
          <div className='mt-[220px] px-8 relative' style={{ zIndex: 4 }}>
            <h1 className='text-5xl text-white absolute top-[-90px] left-[260px] dancing-script-text font-thin'>For,</h1>
            <h1 className='text-5xl text-[#eeff41] absolute top-[-45px] left-[335px] great-vibes-text font-thin text-shadow-lg shadow-gray-700'>Kara</h1>
          </div>
          <div 
            className='w-[100px] h-[100px] absolute top-[50px] left-[590px] border-2 border-[#bf38b2]'
            style={{ 
              backgroundImage: `url(${Bg})`, 
              backgroundSize: 'cover', 
              backgroundPosition: 'center',
              zIndex: 5
            }}
          />
        </div>
      </motion.div>
      <AnimatePresence>
        {showForm && (
          <motion.div 
            initial={{ opacity: 0, z: -300 }}
            animate={{ 
              opacity: 1,
              z: 0
            }}
            transition={{ duration: 1 }}
            className='absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2'
            style={{ zIndex: 10 }}
          >
            <Form />
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  )
}

export default Home
