import React, { useState } from 'react'
import Bg from '../Images/giphy.gif'

const Form = () => {
    const [buttonSize, setButtonSize] = useState(150)

    const handleButtonClick = (isYes) => {
      setButtonSize(prevSize => {
        const newSize = isYes ? prevSize + 20 : prevSize - 30
        return Math.min(Math.max(newSize, 0), 150) 
      })
    }

  return (
    <div className='flex h-screen items-center justify-center'>
      <div className='flex-col flex w-[600px] h-[660px] bg-[#ffc0cb] kaushan-script-text pt-[30px]'>
        <div className='w-full h-[150px] flex items-center justify-center pr-[45px]'>
          <h1 className='text-[#ff0000] text-[75px]'>Will you be my</h1>
        </div>
        <div className= 'border-2 border-black w-full h-[100px] flex items-center justify-end pr-[45px]'>
          <h1 className='text-[#ff0000] text-[75px] pr-[30px] pb-[60px]'>valentine? </h1>
        </div>
        <div className='flex flex-row border-2 border-white w-full h-full'>
          <div className='w-[50%] h-full flex items-center justify-center pb-[100px]'>
            <div 
              className='w-[210px] h-[270px]'
              style={{ backgroundImage: `url(${Bg})`, backgroundSize: 'cover', backgroundPosition: 'center' }}
            />
          </div>
          <div className='flex flex-col items-center justify-center border-2 border-blue-500 w-[50%] gap-10 pb-[100px]'>
            <button 
              className='bg-[#ff0000] rounded-md text-white text-3xl'
              style={{
                width: `${150 - buttonSize}px`,
                height: `${(150 - buttonSize) * 0.625}px`,
                fontSize: `${(150 - buttonSize) * 0.25}px`,
                transition: 'all 0.3s ease-in-out'
              }}
              onClick={() => handleButtonClick(true)}
            >
              Yes !
            </button>
            <button 
              className='bg-white text-[#ff0000] rounded-md text-3xl'
              style={{
                width: `${buttonSize}px`,
                height: `${buttonSize * 0.625}px`,
                fontSize: `${buttonSize * 0.25}px`,
                transition: 'all 0.3s ease-in-out'
              }}
              onClick={() => handleButtonClick(false)}
            >
              No
            </button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Form
