import React, { useState } from 'react'
import Bg from '../Images/giphy.gif'

const Form = () => {
  const [noButtonSize, setNoButtonSize] = useState(100) // Start at 100% size

  const handleNoClick = () => {
    setNoButtonSize(prevSize => {
      const newSize = prevSize - 10 // Decrease by 10% each click
      return newSize > 0 ? newSize : 0 // Don't go below 0
    })
  }

  return (
    <div className='flex h-screen items-center justify-center'>
      <div className='flex-col flex w-[600px] h-[660px] bg-[#ffc0cb] kaushan-script-text pt-[30px]'>
        {/* ... other parts of your JSX ... */}
        <div className='flex flex-col items-center justify-center border-2 border-blue-500 w-[50%] gap-10 pb-[100px]'>
          <button className='w-[120px] h-[75px] bg-[#ff0000] rounded-md text-white text-3xl'>Yes !</button>
          {noButtonSize > 0 && (
            <button 
              className='bg-white text-[#ff0000] rounded-md text-3xl'
              style={{
                width: `${noButtonSize}px`,
                height: `${noButtonSize * 0.625}px`, // Maintain aspect ratio
                fontSize: `${noButtonSize * 0.25}px` // Scale font size with button
              }}
              onClick={handleNoClick}
            >
              No
            </button>
          )}
        </div>
      </div>
    </div>
  )
}

export default Form
